The following code example demonstrates ReplaceElement method. Also the following code uses InsertElement method.

// get document
Document doc = tab.MainDocument;

// find first element
HtmlElement he1 = instance.GetTabByAddress("page").GetDocumentByAddress("0").FindElementByAttribute("input:checkbox", "fulltag", "input:checkbox", "text", 0);
// find second element
HtmlElement he2 = instance.GetTabByAddress("page").GetDocumentByAddress("0").FindElementByName("rad");

// if both elements are exist then replace he1 to he2
if (!he1.IsVoid && !he2.IsVoid) doc.ReplaceElement(he2, h1);
// get document
$doc = $tab->MainDocument;

// find first element
$he1 = $instance->GetTabByAddress("page")->GetDocumentByAddress("0")->FindElementByAttribute("input:checkbox", "fulltag", "input:checkbox", "text", 0);
// find second element
$he2 = $instance->GetTabByAddress("page")->GetDocumentByAddress("0")->FindElementByName("rad");

// if both elements are exist then replace he1 to he2
if (!$he1->IsVoid && !$he2->IsVoid) $doc->ReplaceElement($he2, $h1);

The following code example demonstrates several properties and methods of the DocumentCollection.

using System;
using ZennoLab.CommandCenter;

namespace Sample
{
    internal class Example
    {
        public static int Execute(Instance instance, IZennoPosterProjectModel project)
        {
            // clear cookie
            instance.ClearCookie();

            // go to zennolab.com
            Tab tab = instance.MainTab;
            if ((tab.IsVoid) || (tab.IsNull)) return -1;
            if (tab.IsBusy) tab.WaitDownloading();
            tab.Navigate("zennolab.com");
            if (tab.IsBusy) tab.WaitDownloading();

            // get all documents
            DocumentCollection docCol = tab.AllDocuments;

            // if any document exist
            if ((docCol.ErrorDetected) || (docCol.IsVoid)) return -1;

            Document doc = null;

            for (int i = 0; i < docCol.Count; i++) doc = docCol.Documents[i];

            // get index of document
            int index = docCol.IndexOf(doc);

            return 0;
        }
    }
}
<?php
namespace System;
namespace ZennoLab\CommandCenter;

class Example
{
    public static function Execute($instance, $project)
    {
        // clear cookie
        $instance->ClearCookie();

        // go to zennolab.com
        $tab = $instance->MainTab;
        if (($tab->IsVoid) || ($tab->IsNull)) return -1;
        if ($tab->IsBusy) $tab->WaitDownloading();
        $tab->Navigate("zennolab.com");
        if ($tab->IsBusy) $tab->WaitDownloading();

        // get all documents
        $docCol = $tab->AllDocuments;

        // if any document exist
        if (($docCol->ErrorDetected) || ($docCol->IsVoid)) return -1;

        for ($i = 0; $i < $docCol->Count; $i++) $doc = $docCol->Documents[$i];

        // get index of document
        $index = $docCol->IndexOf($doc);

        return 0;
    }
}
?>

