    if ($tab->IsBusy) $tab->WaitDownloading();

    // find element
    $he = $tab->GetDocumentByAddress("0")->FindElementByAttribute("input:checkbox", "fulltag", "input:checkbox", "text", 0);

    // if error detected then exit with -1
    if ($he->ErrorDetected) return -1;

    return 0;
}

